// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

contract Comparison {
    function lt(uint256 _a, uint256 _b) external view returns (bool) {
        return _a < _b;
    }

    function gt(uint256 _a, uint256 _b) external view returns (bool) {
        return _a > _b;
    }

    function eq(uint256 _a, uint256 _b) external view returns (bool) {
        return _a == _b;
    }

    function ne(uint256 _a, uint256 _b) external view returns (bool) {
        return _a != _b;
    }

    function lte(uint256 _a, uint256 _b) external view returns (bool) {
        return _a <= _b;
    }

    function gte(uint256 _a, uint256 _b) external view returns (bool) {
        return _a >= _b;
    }
}
